import { Component, OnInit } from '@angular/core';
import { Router } from '@angular/router';
import { FormBuilder, Validators } from '@angular/forms';
import { Observable } from 'rxjs';

import { Division } from './division.model';
import { DivisionService } from './division.service';

@Component({
  selector: 'app-division',
  templateUrl: './division.component.html',
  styleUrls: ['./division.component.css']
})
export class DivisionComponent implements OnInit {
  dataSaved = false;
  divisionForm: any;

  allDivision: Observable<any> = this.divisionService.getAllDivisions();


  divisionIdUpdate: any = null;
  message = "";

  constructor( private formBuilder: FormBuilder, private router: Router,
    private divisionService: DivisionService) { }

  ngOnInit() {
    this.divisionForm = this.formBuilder.group({
      name: ['', [Validators.required]]
    });
    this.loadAllDivision();
  }

  loadAllDivision() {
    this.allDivision = this.divisionService.getAllDivisions();
  }

  onFormSubmit() {
    this.dataSaved = false;
    const division = this.divisionForm.value;
    this.createDivision(division);
    this.divisionForm.reset();
  }

  loadDivisionToEdit(id: any) {
    this.divisionService.getDivisionById(id).subscribe(division => {
      this.message = "";
      this.dataSaved = false;
      this.divisionIdUpdate = division.divisionId;
      this.divisionForm.get('name').setValue(division['name']);
    });
  }

  createDivision(division: Division) {
    if (this.divisionIdUpdate == null) {
      this.divisionService.createDivision(division).subscribe(() => {
        this.dataSaved = true;
        this.message = 'Record Saved Successfully.';
        this.loadAllDivision();
        this.divisionIdUpdate = null;
        this.divisionForm.reset();
      }
      )

    }
    else {
      division.divisionId = this.divisionIdUpdate;
      this.divisionService.updateDivision(this.divisionIdUpdate, division).subscribe(() => {
        this.dataSaved = true;
        this.message = "Record Updated Successfully.";
        this.loadAllDivision();
        this.divisionIdUpdate = null;
        this.divisionForm.reset();
      });
    }
  }

  deleteDivision(id: string) {
    if (confirm("Are You Sure To Delete This?")) {
      this.divisionService.deleteDivisionById(id).subscribe(() => {
        this.dataSaved = true;
        this.message = "Record Deleted Successfully.";
        this.loadAllDivision();
        this.divisionIdUpdate = null;
        this.divisionForm.reset();
      });
    }
  }

  resetForm() {
    this.divisionForm.reset();
    this.message = "";
    this.dataSaved = false;
  }
}
<div class="container" style="width:100%">
  <form [formGroup]="divisionForm" (ngSubmit)="onFormSubmit()">
    <div class="card">
      <div class="card-header bg-info">
        <h2 class="m-b-0 text-white">
          <i class="fa fa-plus-circle" style="font-size:20px; margin:5px;"></i><small>Division Information</small>
        </h2>
      </div>
      <div>

        <div style="width:50%">
          <div>
            <table>
              <tr>
                <td class="tbl1">
                  <div class="demo-full-width">
                    <input formControlName="name" type="text" placeholder="Name" class="form-control">
                  </div>
                  <div>
                    <span *ngIf="!divisionForm.get('name').value && divisionForm.get('name').touched"></span>
                  </div>
                </td>
              </tr>

              <tr>
                <td></td>
                <td class="content-center btn-toolbar">
                </td>
                <td>
                  <p *ngIf="dataSaved" style="color:rgb(0, 128, 0);font-size:20px;font-weight:bold" Class="success" align="left">
                    {{message}}
                  </p>
                </td>
              </tr>

            </table>

          </div>
          <div>
            <button class="btn btn-primary" type="submit" [disabled]="!divisionForm.valid" style="background-color:darkblue; color:white">Submit</button>&nbsp;
            <button class="btn btn-info" type="reset" (click)="resetForm()">Reset</button>
          </div>
        </div>

        <table class="table">
          <tr ngclass="btn-primary">
            <th class="tbl2">Division Name</th>
            <th class="tbl2">Edit</th>
            <th class="tbl2">Delete</th>
          </tr>

          <tr *ngFor="let division of allDivision | async">
            <td class="tbl2">{{ division.name }}</td>
            <td class="tbl2">
              <button type="button" class="btn btn-info" (click)="loadDivisionToEdit(division.divisionId)">Edit</button>
            </td>
            <td class="tbl2">
              <button type="button" class="btn btn-danger" (click)="deleteDivision(division.divisionId)">Delete</button>
            </td>
          </tr>
        </table>
      </div>
    </div>

  </form>

</div>
