import { Component, OnInit } from '@angular/core';
import { Router } from '@angular/router';
import { Brand } from './brand.model';
import { BrandService } from './brand.service';
import { from, Observable } from 'rxjs';
import { FormBuilder, Validators } from '@angular/forms';


@Component({
  selector: 'app-brand',
  templateUrl: './brand.component.html',
  styleUrls: ['./brand.component.css']
})
export class BrandComponent implements OnInit {
  dataSaved = false;
  brandForm: any;

  allBrand: Observable<any> = this.brandService.getAllBrand();
  

  brandIdUpdate = null;
  message = "";

  constructor(private formBuilder: FormBuilder, private routes: Router,
    private brandService: BrandService) { }

  ngOnInit() {
    this.brandForm = this.formBuilder.group({
      name: ['', [Validators.required]],
      description: ['', [Validators.required]],
      isActive: [false]
    });
    this.loadAllBrand();
  }

  loadAllBrand() {
    this.allBrand = this.brandService.getAllBrand();
  }

  onFormSubmit() {
    this.dataSaved = false;
    const brand = this.brandForm.value;
    this.createBrand(brand);
    this.brandForm.reset();
  }

  loadBrandToEdit(brandId: any) {
    this.brandService.getBrandById(brandId).subscribe(brand => {
      this.message = "";
      this.dataSaved = false;
      this.brandIdUpdate = brand.brandId;
      this.brandForm.get('name').setValue(brand['name']);
      this.brandForm.get('description').setValue(brand['description']);
      this.brandForm.get('isActive').setValue(brand['isActive']);
    });
  }

  createBrand(brand: Brand) {
    if (this.brandIdUpdate == null) {
      this.brandService.createBrand(brand).subscribe(() => {
        this.dataSaved = true;
        this.message = "Record Saved Successfully.";
        this.loadAllBrand();
        this.brandIdUpdate = null;
        this.brandForm.reset();
      }
      )
    }
    else {
      brand.brandId = this.brandIdUpdate;
      this.brandService.updateBrand(this.brandIdUpdate, brand).subscribe(() => {
        this.dataSaved = true;
        this.message = "Record Update Successfully.";
        this.loadAllBrand();
        this.brandIdUpdate = null;
        this.brandForm.reset();

      });
    }
  }


  deleteBrand(id: string) {
    if (confirm("Are You Sure To Delete This?")) {
      this.brandService.deleteBrandById(id).subscribe(() => {
        this.dataSaved = true;
        this.message = "Record Deleted Successfully.";
        this.loadAllBrand();
        this.brandIdUpdate = null;
        this.brandForm.reset();
      })
    }
  }

  resetForm() {
    this.brandForm.reset();
    this.message = "";
    this.dataSaved = false;
  }

}

<div class="container" style="width:100%">
  <form [formGroup]="brandForm" (ngSubmit)="onFormSubmit()">
    <div class="card">
      <div class="card-header bg-info">
        <h2 class="m-b-0 text-white">
          <i class="fa fa-plus-circle" style="font-size:20px; margin:5px;"></i><small>Brand Information</small>
        </h2>
      </div>
      <div>

        <div style="width:50%">
          <div>
            <table>
              <tr>
                <td class="tbl1">
                  <div class="demo-full-width">
                    <input formControlName="name" type="text" placeholder="Brand Name" class="form-control">
                  </div>
                  <div>
                    <span *ngIf="!brandForm.get('name').value && brandForm.get('name').touched"></span>
                  </div>
                </td>
              </tr>
              <tr>
                <td class="tbl1">
                  <div class="demo-full-width">
                    <input formControlName="description" type="text" placeholder="Description" class="form-control">
                  </div>
                  <div>
                    <span *ngIf="!brandForm.get('description').value && brandForm.get('description').touched"></span>
                  </div>
                </td>
              </tr>

              <tr>
                <td class="form-check">
                  <input class="form-check-input" type="checkbox" formControlName="isActive" name="IsActive" />
                  <label class="form-check-label" for="IsActive">
                    Is Active?
                  </label>
                </td>
              </tr>

              <tr>
                <td></td>
                <td class="content-center btn-toolbar">
                </td>
                <td>
                  <p *ngIf="dataSaved" style="color:rgb(0, 128, 0);font-size:20px;font-weight:bold" Class="success" align="left">
                    {{message}}
                  </p>
                </td>
              </tr>

            </table>

          </div>
          <div>
            <button class="btn btn-primary" type="submit" [disabled]="!brandForm.valid" style="background-color:darkblue; color:white">Submit</button>&nbsp;
            <button class="btn btn-info" type="reset" (click)="resetForm()">Reset</button>
          </div>
        </div>

        <table class="table">
          <tr ngclass="btn-primary">
            <th class="tbl2">Brand Name</th>
            <th class="tbl2">Description</th>
            <th class="tbl2">Is Active?</th>
            <th class="tbl2">Edit</th>
            <th class="tbl2">Delete</th>
          </tr>
          <tr *ngFor="let brand of allBrand | async">
            <td class="tbl2">{{brand.name}}</td>
            <td class="tbl2">{{brand.description}}</td>
            <td class="tbl2">{{brand.isActive}}</td>
            <td class="tbl2">
              <button type="button" class="btn btn-info" (click)="loadBrandToEdit(brand.brandId)">Edit</button>
            </td>
            <td class="tbl2">
              <button type="button" class="btn btn-danger" (click)="deleteBrand(brand.brandId)">Delete</button>
            </td>
          </tr>
        </table>
      </div>
    </div>

  </form>

</div>
