import { Component, OnInit } from '@angular/core';
import { Router } from '@angular/router';
import { Category } from './category.model';
import { CategoryService } from './category.service';
import { FormBuilder, Validators } from '@angular/forms';
import { Observable } from 'rxjs';

@Component({
  selector: 'app-category',
  templateUrl: './category.component.html',
  styleUrls: ['./category.component.css']
})
export class CategoryComponent implements OnInit {
  dataSaved = false;
  categoryForm = this.formBuilder.group({
    name: ['', [Validators.required]],
    description: ['', [Validators.required]],
    isActive: [false]
  });

  allCategories: Observable<Category[]> = this.categoryService.getCategories();
  
  categoryIdToUpdate: number | null = null;
  message = "";

  constructor(
    private formBuilder: FormBuilder,
    private router: Router,
    private categoryService: CategoryService
  ) { }

  ngOnInit(): void {
    this.loadAllCategories();
  }

  loadAllCategories(): void {
    this.allCategories = this.categoryService.getCategories();
  }

  // 
  onFormSubmit(): void {
    this.dataSaved = false;
  
    // Type assertion to ensure the form value matches the Category type
    const category: Category = this.categoryForm.value as Category;
  
    this.addCategory(category);
    this.categoryForm.reset();
  }
  
  loadCategoryToEdit(categoryId: number): void {
    this.categoryService.getCategoryId(categoryId).subscribe(category => {
      this.message = "";
      this.dataSaved = false;
      this.categoryIdToUpdate = category.categoryId;
      this.categoryForm.patchValue({
        name: category.name,
        description: category.description,
        isActive: category.isActive
      });
    });
  }

  addCategory(category: Category): void {
    if (this.categoryIdToUpdate === null) {
      this.categoryService.addCategory(category).subscribe(() => {
        this.dataSaved = true;
        this.message = "Category Saved Successfully.";
        this.loadAllCategories();
        this.categoryIdToUpdate = null;
        this.categoryForm.reset();
      });
    } else {
      category.categoryId = this.categoryIdToUpdate;
      this.categoryService.updateCategory(this.categoryIdToUpdate, category).subscribe(() => {
        this.dataSaved = true;
        this.message = "Category Updated Successfully.";
        this.loadAllCategories();
        this.categoryIdToUpdate = null;
        this.categoryForm.reset();
      });
    }
  }

  

  deleteCategory(id: number): void {
    if (confirm("Are you sure you want to delete this category?")) {
      this.categoryService.deleteCategory(id).subscribe(() => {
        this.dataSaved = true;
        this.message = "Category Deleted Successfully.";
        this.loadAllCategories();
        this.categoryIdToUpdate = null;
        this.categoryForm.reset();
      });
    }
  }

  resetForm(): void {
    this.categoryForm.reset();
    this.message = "";
    this.dataSaved = false;
  }
}
