// src/app/cart/cart.component.ts
import { Component, OnInit } from '@angular/core';
import { CartService } from './cart.service';
import { CartItem } from './cartItem.model';

@Component({
  selector: 'app-cart',
  templateUrl: './cart.component.html',
  styleUrls: ['./cart.component.css'] // Optional: Create a CSS file if needed
})
export class CartComponent implements OnInit {
  cartItems: CartItem[] = [];
  cartId: string = ''; // Retrieve or set this based on your application logic
  totalAmount: number = 0;

  constructor(private cartService: CartService) {}

  ngOnInit(): void {
    this.cartId = localStorage.getItem('cartId') || ''; // Get cartId from local storage
    this.loadCartItems();
  }

  loadCartItems(): void {
    if (this.cartId) {
      this.cartService.getCartItems(this.cartId).subscribe((items: CartItem[]) => {
        this.cartItems = items;
        this.calculateTotal();
      });
    }
  }

  updateQuantity(item: CartItem, quantity: number): void {
    item.quantity = quantity;
    this.cartService.updateCartItem(this.cartId, this.cartItems).subscribe(() => {
      this.calculateTotal();
    });
  }

  removeItem(item: CartItem): void {
    this.cartItems = this.cartItems.filter(i => i.productId !== item.productId);
    this.cartService.updateCartItem(this.cartId, this.cartItems).subscribe(() => {
      this.calculateTotal();
    });
  }

  calculateTotal(): void {
    this.totalAmount = this.cartItems.reduce((total, item) => total + item.price * item.quantity, 0);
  }

  clearCart(): void {
    this.cartService.clearCart(this.cartId).subscribe(() => {
      this.cartItems = [];
      this.totalAmount = 0;
      localStorage.removeItem('cartId');
    });
  }

  proceedToCheckout(): void {
    // Logic to proceed to checkout
  }
}
<!-- src/app/cart/cart.component.html -->
<div class="container mt-4">
  <h2>Your Shopping Cart</h2>

  <div *ngIf="cartItems && cartItems.length > 0; else emptyCart">
    <table class="table table-bordered">
      <thead>
        <tr>
          <th>Product</th>
          <th>Price</th>
          <th>Quantity</th>
          <th>Total</th>
          <th>Actions</th>
        </tr>
      </thead>
      <tbody>
        <tr *ngFor="let item of cartItems">
          <td>{{ item.product.name }}</td> <!-- Access the product name here -->
          <td>{{ item.price | currency }}</td>
          <td>
            <input type="number" [(ngModel)]="item.quantity" (change)="updateQuantity(item, item.quantity)" min="1" class="form-control">
          </td>
          <td>{{ item.price * item.quantity | currency }}</td>
          <td>
            <button class="btn btn-danger" (click)="removeItem(item)">Remove</button>
          </td>
        </tr>
      </tbody>
    </table>

    <div class="text-right">
      <h3>Total: {{ totalAmount | currency }}</h3>
      <button class="btn btn-primary" (click)="proceedToCheckout()">Proceed to Checkout</button>
      <button class="btn btn-warning" (click)="clearCart()">Clear Cart</button>
    </div>
  </div>

  <ng-template #emptyCart>
    <p>Your cart is empty.</p>
  </ng-template>
</div>
