// src/app/product/product.component.ts
import { Component, OnInit } from '@angular/core';
import { ProductService } from './product.service';
import { CartService } from '../cart/cart.service';
import { Product } from './product.model';

@Component({
  selector: 'app-product',
  templateUrl: './product.component.html',
  styleUrls: ['./product.component.css']
})
export class ProductComponent implements OnInit {
  products: Product[] = [];
  selectedProduct?: Product;
  quantities: { [key: number]: number } = {}; // Keeps track of quantities for each product

  constructor(private productService: ProductService, private cartService: CartService) { }

  ngOnInit(): void {
    this.loadProducts();
  }

  loadProducts(): void {
    this.productService.getProducts().subscribe(
      (data) => {
        this.products = data;
        // Initialize quantities for each product
        this.products.forEach(product => {
          this.quantities[product.productId] = 1; // Default quantity is 1
        });
      },
      (error) => {
        console.error('Error loading products', error);
      }
    );
  }

  addToCart(product: Product): void {
    const quantity = this.quantities[product.productId] || 1; // Default to 1 if not set
    this.cartService.addProductToCart(product, quantity);
    // alert(`${quantity} of ${product.name} added to cart!`);
  }

  selectProduct(product: Product): void {
    this.selectedProduct = product;
  }
}
