import { Component, OnInit } from '@angular/core';
import { AuthService } from '../Login/auth.service';
import { CartService } from '../cart/cart.service';


import { Router } from '@angular/router';

@Component({
  selector: 'app-navbar',
  templateUrl: './nav.component.html',
  styleUrls: ['./nav.component.css']
})
export class NavComponent implements OnInit {
  user: any; // Stores the logged-in user details
  cartId: string | null = null;
  cartItemCount: number = 0;

  constructor(public authService: AuthService,private router: Router,private cartService: CartService, ) {};
 

  ngOnInit(): void {
    this.loadUser();
    this.updateCartItemCount();
 
  }

  // Check if the user is logged in
  isLoggedIn(): boolean {
    return this.authService.isAuthenticated();
  }

  // Load the user details from the AuthService
  loadUser(): void {
    this.user = this.authService.getUser();
    console.log('User:', this.user); // Check if user object is correctly populated
  }

  // Handle logout
  logout(): void {
    this.authService.logout();
    this.loadUser(); // Reset user after logout
  }
  getCartItemCount(): void {
    this.cartService.getCartItemCount().subscribe(count => {
      this.cartItemCount = count;
    });
  }

  navigateToCart(): void {
    this.router.navigate(['/cart']);
  }

  updateCartItemCount(): void {
    this.cartService.getCartItemCount().subscribe(count => {
      console.log('Cart item count:', count); // Debug log
      this.cartItemCount = count;
    });}
}


<nav class="navbar navbar-expand-lg">
  <a class="navbar-name" href="#">Solaly</a>
  
  <button class="navbar-toggler" type="button" data-toggle="collapse" data-target="#navbarNav" aria-controls="navbarNav" aria-expanded="false" aria-label="Toggle navigation">
    <span class="navbar-toggler-icon"></span>
  </button>

  <div class="collapse navbar-collapse" id="navbarNav">
    <ul class="navbar-nav ">
      <li class="nav-item">
        <!-- <a routerLink="/cart" class="nav-link">cart </a> -->
        
        <button (click)="navigateToCart()" class="basket-button">
          <i class="fas fa-shopping-basket">cart: </i>
          <span class="basket-count">{{ cartItemCount }}</span>
        </button>
        
      </li>
      
      <li class="nav-item">
        <a routerLink="/user" class="nav-link">Register</a>
      </li>

      <li class="nav-item"  *ngIf="!isLoggedIn()"  >
        <a routerLink="/login" class="nav-link" class="btn btn-primary">Login</a>
      </li>
      <li class="nav-item" *ngIf="isLoggedIn()">
        <a class="nav-link">Welcome, {{ user?.email }}</a>
      </li>
      <li class="nav-item" *ngIf="isLoggedIn()">
        <a class="btn btn-danger" (click)="logout()">Logout</a>
      </li>
    </ul>
  </div>
</nav>
