// src/app/components/cart/cart.component.ts

import { Component, OnInit } from '@angular/core';
import { CartService } from '../cart/cart.service';
import { Product } from '../product/product.model';
import {CartItem} from '../cart/cartItem.model'

@Component({
  selector: 'app-cart',
  templateUrl: './cart.component.html',
  styleUrls: ['./cart.component.css']
})
export class CartComponent implements OnInit {
  cartCount: number = 0;
  cartItems: Product[] = [];
  cartId: string = ''; // Store the current cartId her
  latestCartItem: CartItem | null = null;


  constructor(private cartService: CartService) { }

  ngOnInit(): void {
    this.getCartCount();
    this.cartId = localStorage.getItem('currentCartId') || ''; 
    this.navigateToCart();
    this.getStoredCartItems();
    this.cartService.getLatestCartItem().subscribe(item => {
      this.latestCartItem = item;
    }, error => {
      console.error('Error fetching latest cart item:', error);
    });

  }
  getCartCount(): void {
    this.cartService.getCartItemCount().subscribe(count => {
      this.cartCount = count;
    });
  }

  private getStoredCartItems(): Product[] {
    const storedItems = localStorage.getItem('cartItems');
    return storedItems ? JSON.parse(storedItems) : [];
  }

  navigateToCart(): void {
    if (this.cartId) {
      this.cartService.getCartItemsByCartId(this.cartId).subscribe(items => {
        this.cartItems = items;
      }, error => {
        console.error('Error fetching cart items:', error);
      });
    }
  }
}
